//import data from './products.json'

const products = {
  product1: {
    img: "./assets/monitor1.jpg",
    description:
      "Monitor LED 27'' Gamer Curvo Samsung 1920 x 1080 FHD 240Hz HDMI, DP, Gsync Série CRG50",
    price: "R$2813,99",
    bestPrice: "R$2599,00",
    portion: "R$259,90",
  },
  product2: {
    img: "./assets/monitor2.jpg",
    description:
      "Monitor LED 24'' Gamer Samsung LS24D332HSX/ZD 1920x1080 1ms 75hz",
    price: "R$1499,99",
    bestPrice: "R$1424,00",
    portion: "R$142,40",
  },
  product3: {
    img: "./assets/monitor3.jpg",
    description:
      "Monitor LED 27'' Dell Professional IPS P2719H Full HD - Preto",
    price: "R$1699,99",
    bestPrice: "R$1599,00",
    portion: "R$159,90",
  },
  product4: {
    img: "./assets/monitor4.png",
    description:
      "Monitor LED 29'' LG Ultrawide 21:9 com HDR 10 IPS Full HD - 29WK600",
    price: "R$1899,99",
    bestPrice: "R$1799,00",
    portion: "R$179,90",
  },
};

const CardBory = ({desc}) => {
  return (
    <div className="card-body">
      <h5 className="card-title">{products.product1.description}</h5>
      <p className="card-text">{products.product1.price}</p>
      <p className="card-text">{products.product1.bestPrice}</p>
      <p className="card-text">
        em até <b>10x de {products.product1.portion}</b> sem juros
      </p>
      <a href="#" className="btn btn-primary">
        ADICIONAR
      </a>
    </div>
  );
};

export default CardBory;
